import React, {useState, useEffect } from "react";
import './css/GetGeckoApi.css';
import { DataGrid } from '@mui/x-data-grid';
const url = "https://api.coingecko.com/api/v3/coins/markets?vs_currency=eur&order=market_cap_desc&per_page=10&page=1&sparkline=false";


const GetGeckoApi = () => {

    const [marketTableData, setmarketTableData] = useState([]);

    const getMarketData = async () => {
        const response = await fetch(url);
        const Data = await response.json();
        setmarketTableData(Data);
}

useEffect(() => {
    getMarketData();
},[]);

console.log(marketTableData);

  return (
    <div className="container">
        <div className="header-div">
            <h2>Welcome to CoinGecko Data Market APIs</h2>
            <p> Following API Results are ordered by <strong>Market Cap Description</strong> and using Euro as default <strong>VS_Currency</strong> </p>
            <p>Please Enter the Coin ID below to get the results by Coin ID</p>
        </div>
        <input type="text" placeholder="Please Enter Coin Id here"></input>
        <div className="results-div">
            <DataGrid
            rows={marketTableData} columns={marketDataCols} pageSize={12}/>
        </div>
    
    </div>  

  )
}

export default GetGeckoApi;
